import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('menu');
  const [menu, setMenu] = useState([]);
  const [tables, setTables] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTable, setSelectedTable] = useState(null);
  const [cart, setCart] = useState([]);

  // API base URL
  const API_BASE = 'http://localhost:5001/api';

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [menuRes, tablesRes, ordersRes] = await Promise.all([
        axios.get(`${API_BASE}/menu`),
        axios.get(`${API_BASE}/tables`),
        axios.get(`${API_BASE}/orders`)
      ]);

      setMenu(menuRes.data);
      setTables(tablesRes.data);
      setOrders(ordersRes.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch data. Please check if the backend server is running.');
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  const addToCart = (item) => {
    const existingItem = cart.find(cartItem => cartItem.id === item.id);
    if (existingItem) {
      setCart(cart.map(cartItem => 
        cartItem.id === item.id 
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
  };

  const updateQuantity = (itemId, newQuantity) => {
    if (newQuantity <= 0) {
      setCart(cart.filter(item => item.id !== itemId));
    } else {
      setCart(cart.map(item => 
        item.id === itemId ? { ...item, quantity: newQuantity } : item
      ));
    }
  };

  const placeOrder = async () => {
    if (!selectedTable || cart.length === 0) {
      alert('Please select a table and add items to cart');
      return;
    }

    try {
      const orderData = {
        table_id: selectedTable,
        items: cart.map(item => ({
          menu_item_id: item.id,
          quantity: item.quantity
        }))
      };

      const response = await axios.post(`${API_BASE}/orders`, orderData);
      alert(`Order placed successfully! Order ID: ${response.data.order_id}`);

      // Clear cart and refresh data
      setCart([]);
      setSelectedTable(null);
      fetchData();
    } catch (err) {
      alert('Failed to place order. Please try again.');
      console.error('Error placing order:', err);
    }
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      await axios.put(`${API_BASE}/orders/${orderId}/status`, { status: newStatus });
      fetchData();
    } catch (err) {
      alert('Failed to update order status');
      console.error('Error updating order status:', err);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading restaurant management system...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error">{error}</div>
        <button className="button" onClick={fetchData}>Retry</button>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="header">
        <h1>üçΩÔ∏è Restaurant Management System</h1>
        <div className="nav">
          <button 
            className={`nav-button ${activeTab === 'menu' ? 'active' : ''}`}
            onClick={() => setActiveTab('menu')}
          >
            Menu
          </button>
          <button 
            className={`nav-button ${activeTab === 'tables' ? 'active' : ''}`}
            onClick={() => setActiveTab('tables')}
          >
            Tables
          </button>
          <button 
            className={`nav-button ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            Orders
          </button>
          <button 
            className={`nav-button ${activeTab === 'pos' ? 'active' : ''}`}
            onClick={() => setActiveTab('pos')}
          >
            POS
          </button>
        </div>
      </div>

      {/* Menu Section */}
      {activeTab === 'menu' && (
        <div className="section">
          <h2>Menu Items</h2>
          <div className="grid">
            {menu.map(item => (
              <div key={item.id} className="card">
                <h3>{item.name}</h3>
                <p>{item.description}</p>
                <p><strong>Category:</strong> {item.category}</p>
                <div className="price">${item.price.toFixed(2)}</div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Tables Section */}
      {activeTab === 'tables' && (
        <div className="section">
          <h2>Table Management</h2>
          <div className="table-grid">
            {tables.map(table => (
              <div key={table.id} className="table-card">
                <div className="table-number">Table {table.number}</div>
                <div className="capacity">Capacity: {table.capacity}</div>
                <div className={`status ${table.status}`}>
                  {table.status}
                </div>
                {table.current_order_id && (
                  <p>Order #{table.current_order_id}</p>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Orders Section */}
      {activeTab === 'orders' && (
        <div className="section">
          <h2>Order Management</h2>
          <div className="grid">
            {orders.map(order => (
              <div key={order.id} className="card">
                <h3>Order #{order.id}</h3>
                <p><strong>Table:</strong> {order.table_id}</p>
                <p><strong>Status:</strong> {order.status}</p>
                <p><strong>Total:</strong> ${order.total_amount.toFixed(2)}</p>
                <p><strong>Created:</strong> {new Date(order.created_at).toLocaleString()}</p>

                <div>
                  <strong>Items:</strong>
                  {order.items.map(item => (
                    <div key={item.id} className="order-item">
                      <span>{item.menu_item_name}</span>
                      <span>Qty: {item.quantity}</span>
                      <span>${item.price.toFixed(2)}</span>
                    </div>
                  ))}
                </div>

                <div style={{ marginTop: '15px' }}>
                  <select 
                    value={order.status} 
                    onChange={(e) => updateOrderStatus(order.id, e.target.value)}
                    style={{ marginRight: '10px' }}
                  >
                    <option value="pending">Pending</option>
                    <option value="preparing">Preparing</option>
                    <option value="ready">Ready</option>
                    <option value="served">Served</option>
                    <option value="paid">Paid</option>
                  </select>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* POS Section */}
      {activeTab === 'pos' && (
        <div className="section">
          <h2>Point of Sale</h2>

          <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '30px' }}>
            {/* Menu Selection */}
            <div>
              <h3>Select Items</h3>
              <div className="grid">
                {menu.map(item => (
                  <div key={item.id} className="card" style={{ cursor: 'pointer' }} onClick={() => addToCart(item)}>
                    <h3>{item.name}</h3>
                    <p>{item.description}</p>
                    <div className="price">${item.price.toFixed(2)}</div>
                  </div>
                ))}
              </div>
            </div>

            {/* Cart */}
            <div>
              <h3>Current Order</h3>

              <div className="form-group">
                <label>Select Table:</label>
                <select 
                  value={selectedTable || ''} 
                  onChange={(e) => setSelectedTable(parseInt(e.target.value))}
                >
                  <option value="">Choose a table...</option>
                  {tables.filter(table => table.status === 'available').map(table => (
                    <option key={table.id} value={table.id}>
                      Table {table.number}
                    </option>
                  ))}
                </select>
              </div>

              {cart.length > 0 && (
                <>
                  <div>
                    {cart.map(item => (
                      <div key={item.id} className="order-item">
                        <div>
                          <strong>{item.name}</strong>
                          <div>${item.price.toFixed(2)} each</div>
                        </div>
                        <div className="quantity-controls">
                          <button 
                            className="quantity-btn"
                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                          >
                            -
                          </button>
                          <span className="quantity">{item.quantity}</span>
                          <button 
                            className="quantity-btn"
                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                          >
                            +
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>

                  <div className="total">
                    Total: ${cart.reduce((sum, item) => sum + (item.price * item.quantity), 0).toFixed(2)}
                  </div>

                  <button 
                    className="button success" 
                    onClick={placeOrder}
                    disabled={!selectedTable}
                    style={{ width: '100%', marginTop: '20px' }}
                  >
                    Place Order
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
